package com.xmy.quotamanage.service;import com.sun.org.apache.xpath.internal.operations.Quo;import com.xmy.quotamanage.dao.QuotaDao;import com.xmy.quotamanage.entity.Quota;import org.apache.ibatis.annotations.Param;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;/** * @author xmy * @date 2021/4/16 2:30 下午 */@Servicepublic class QuotaService {    @Autowired    QuotaDao quotaDao;    Quota getQuotaById(long id) {        return quotaDao.getQuotaById(id);    }    void updateQuota(Quota quota) {        quotaDao.updateQuota(quota);    }}