package com.xmy.quotamanage.service;import com.xmy.quotamanage.dao.UserDao;import com.xmy.quotamanage.entity.User;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;/** * @author xmy * @date 2021/4/16 1:18 下午 */@Servicepublic class UserService {    @Autowired    UserDao userDao;    public User checkUser(long id) {        return userDao.findMobileAndPassword(id);    }    public List<User> getAllUsers() {        return userDao.getAllUsers();    }}